mapper.select:


    is_delete = 0


        AND id in

            #{item}





     and payer_type = #{payerType}
     and sender_id = #{senderId}
     and receiver_id = #{receiverId}
     and ref1 = #{ref1}
     and ref2 = #{ref2}
     and services_id = #{servicesId}
     and phone = #{phone}
     and postal_code = #{postalCode}
     and batch_id = #{batchId}
     and create_user = #{createUser}
     and update_user = #{updateUser}
     and date_format(created_time,'%Y-%m-%d') = date_format(#{createdTime},'%Y-%m-%d')
     and date_format(updated_time,'%Y-%m-%d') = date_format(#{updatedTime},'%Y-%m-%d')
    and date_format(created_time,'%Y%m') = #{paramMonth}
     and parcel_waybill = #{parcelWaybill}
     and parcel_reference = #{parcelReference}

order by updated_time desc

select pac.id,
pac.payer_type,
pac.sender_id,
pac.receiver_id,
pac.ref1,
pac.ref2,
pac.services_id,
pac.phone,
pac.postal_code,
pac.batch_id,
pac.create_user,
pac.update_user,
pac.created_time,
pac.updated_time,
pac.download_num,
pac.parcel_waybill,
pac.parcel_reference,
pac.is_delete
from mb_package pac
where Not Exists
(
Select rel.new_package_id
from redirect_rel rel
where pac.id = rel.new_package_id
)
and Not Exists
(
Select localRel.old_package_id
from pack_rel_local localRel
where pac.id = localRel.old_package_id
)
and Not Exists
(
Select mrd.platform_trade_code
from mb_return_dto mrd
where pac.parcel_reference = mrd.platform_trade_code
)
and
    pac.is_delete = 0


            AND pac.id in

                #{item}





     and pac.payer_type = #{payerType}
     and pac.sender_id = #{senderId}
     and pac.receiver_id = #{receiverId}
     and pac.ref1 = #{ref1}
     and pac.ref2 = #{ref2}
     and pac.services_id = #{servicesId}
     and pac.phone = #{phone}
     and pac.postal_code = #{postalCode}
     and pac.batch_id = #{batchId}
     and pac.create_user = #{createUser}
     and pac.update_user = #{updateUser}
     and date_format(pac.created_time,'%Y-%m-%d') = date_format(#{createdTime},'%Y-%m-%d')
     and date_format(pac.updated_time,'%Y-%m-%d') = date_format(#{updatedTime},'%Y-%m-%d')
    and date_format(pac.created_time,'%Y%m') = #{paramMonth}
     and pac.parcel_waybill = #{parcelWaybill}
 and pac.parcel_reference = #{parcelReference}
order by updated_time desc

select pac.id,
pac.payer_type,
pac.sender_id,
pac.receiver_id,
pac.ref1,
pac.ref2,
pac.services_id,
pac.phone,
pac.postal_code,
pac.batch_id,
pac.create_user,
pac.update_user,
pac.created_time,
pac.updated_time,
pac.download_num,
pac.parcel_waybill,
