pac.is_delete
from mb_package pac
inner join redirect_rel rel
on pac.id = rel.new_package_id

    pac.is_delete = 0


            AND pac.id in

                #{item}





     and pac.payer_type = #{payerType}
     and pac.sender_id = #{senderId}
     and pac.receiver_id = #{receiverId}
     and pac.ref1 = #{ref1}
     and pac.ref2 = #{ref2}
     and pac.services_id = #{servicesId}
     and pac.phone = #{phone}
     and pac.postal_code = #{postalCode}
     and pac.batch_id = #{batchId}
     and pac.create_user = #{createUser}
     and pac.update_user = #{updateUser}
     and date_format(pac.created_time,'%Y-%m-%d') = date_format(#{createdTime},'%Y-%m-%d')
     and date_format(pac.updated_time,'%Y-%m-%d') = date_format(#{updatedTime},'%Y-%m-%d')
    and date_format(pac.created_time,'%Y%m') = #{paramMonth}
     and pac.parcel_waybill = #{parcelWaybill}
     and pac.parcel_reference = #{parcelReference}

order by updated_time desc

select pac.id,
pac.payer_type,
pac.sender_id,
pac.receiver_id,
pac.ref1,
pac.ref2,
pac.services_id,
pac.phone,
pac.postal_code,
pac.batch_id,
pac.create_user,
pac.update_user,
pac.created_time,
pac.updated_time,
pac.download_num,
pac.parcel_waybill,
pac.parcel_reference,
pac.qty_packages,
pac.is_delete
from mb_package pac
inner join pack_rel_local rel
on pac.id = rel.old_package_id

    pac.is_delete = 0


            AND pac.id in

                #{item}






        and code1 like concat('%', #{code1}, '%') 

        and code2 like concat('%', #{code2}, '%') 

        and code3 like concat('%', #{code3}, '%') 
     and pac.payer_type = #{payerType}
     and pac.sender_id = #{senderId}
     and pac.receiver_id = #{receiverId}
     and pac.ref1 = #{ref1}
     and pac.ref2 = #{ref2}
     and pac.services_id = #{servicesId}
     and pac.phone = #{phone}
     and pac.postal_code = #{postalCode}
     and pac.batch_id = #{batchId}
     and pac.create_user = #{createUser}
     and pac.update_user = #{updateUser}
     and date_format(pac.created_time,'%Y-%m-%d') = date_format(#{createdTime},'%Y-%m-%d')
     and date_format(pac.updated_time,'%Y-%m-%d') = date_format(#{updatedTime},'%Y-%m-%d')
    and date_format(pac.created_time,'%Y%m') = #{paramMonth}
     and pac.parcel_waybill = #{parcelWaybill}
     and pac.parcel_reference = #{parcelReference}
     and pac.qty_packages = #{qtyPackages}

order by updated_time desc

select pac.id,
pac.payer_type,
pac.sender_id,
pac.receiver_id,
pac.ref1,
pac.ref2,
pac.services_id,
pac.phone,
pac.postal_code,
pac.batch_id,
pac.create_user,
pac.update_user,
pac.created_time,
pac.updated_time,
pac.download_num,
pac.parcel_waybill,
pac.parcel_reference,
pac.qty_packages,
pac.is_delete
from mb_package pac
inner join mb_return_dto mrd on pac.parcel_reference = mrd.platform_trade_code

    pac.is_delete = 0


            AND pac.id in

                #{item}






        and mrd.platform_trade_code like concat('%', #{code1}, '%') 


     and pac.payer_type = #{payerType}
     and pac.sender_id = #{senderId}
     and pac.receiver_id = #{receiverId}
     and pac.ref1 = #{ref1}
     and pac.ref2 = #{ref2}
     and pac.services_id = #{servicesId}
     and pac.phone = #{phone}
     and pac.postal_code = #{postalCode}
     and pac.batch_id = #{batchId}
     and mrd.create_by = #{createUser}
     and pac.update_user = #{updateUser}
     and date_format(pac.created_time,'%Y-%m-%d') = date_format(#{createdTime},'%Y-%m-%d')
     and date_format(pac.updated_time,'%Y-%m-%d') = date_format(#{updatedTime},'%Y-%m-%d')
    and date_format(pac.created_time,'%Y%m') = #{paramMonth}
     and pac.parcel_waybill = #{parcelWaybill}
     and pac.parcel_reference = #{parcelReference}
     and pac.qty_packages = #{qtyPackages}

order by updated_time desc

mapper.update:
update mb_package
payer_type = #{payerType}, sender_id = #{senderId}, receiver_id = #{receiverId}, ref1 = #{ref1}, ref2 = #{ref2}, services_id = #{servicesId}, phone = #{phone}, postal_code = #{postalCode}, batch_id = #{batchId}, parcel_waybill = #{parcelWaybill}, parcel_reference = #{parcelReference}, qty_packages = #{qtyPackages}, update_user = #{updateUser}, download_num = #{downloadNum}, updated_time = sysdate(),
where id = #{id}

update mb_package set is_delete = 1, updated_time = sysdate()
where id in

    #{id}

mapper.delete:
delete from mb_package where id in
#{id}
and parcel_waybill is null
